import React, { useState } from "react";

function regex_dist(regex, target) {
  let current = new Array(target.length + 1);
  for (let i = 0; i < target.length + 1; i++) {
    current[i] = new Array(regex.length + 1).fill(0);
  }
  for (let t_i = 0; t_i < target.length + 1; t_i++) {
    for (let r_i = 0; r_i < regex.length + 1; r_i++) {
      if (r_i === 0 && t_i === 0) current[t_i][r_i] = 0;
      else if (r_i === 0) current[t_i][r_i] = t_i;
      else if (t_i === 0) {
        var i = r_i - 1;
        var counter = 0;
        while (i >= 0) {
          var char = regex[i];
          i -= char === "?" || char === "*" || char === "+" ? 2 : 1;
          counter += !(char === "?" || char === "*") ? 1 : 0;
        }
        current[t_i][r_i] = counter;
      } else if (regex[r_i - 1] === ".")
        // Regex special cases
        current[t_i][r_i] = current[t_i - 1][r_i - 1];
      else if (
        regex[r_i - 1] === "+" ||
        regex[r_i - 1] === "*" ||
        regex[r_i - 1] === "?"
      ) {
        if (regex[r_i - 2] === target[t_i - 1]) {
          if (regex[r_i - 1] == "?") {
            current[t_i][r_i] = current[t_i][r_i - 2];
          } else
            current[t_i][r_i] = Math.min(
              current[t_i - 1][r_i - 2],
              current[t_i - 1][r_i]
            );
        } else {
          let additional_cost = regex[r_i - 1] === "+" ? 1 : 0;
          current[t_i][r_i] = Math.min(
            current[t_i - 1][r_i - 2] + 1,
            current[t_i - 1][r_i] + 1,
            current[t_i][r_i - 2] + additional_cost
          );
        }
      } else if (regex[r_i - 1] === target[t_i - 1])
        // Other characters
        current[t_i][r_i] = current[t_i - 1][r_i - 1];
      else {
        current[t_i][r_i] = Math.min(
          current[t_i - 1][r_i - 1] + 1,
          current[t_i - 1][r_i] + 1,
          current[t_i][r_i - 1] + 1
        );
      }
    }
  }
  return current;
}

const App = () => {
  const [regularExpression, setRegularExpression] = useState("");
  const [targetString, setTargetString] = useState("");

  const onSubmitHandler = (event) => {
    event.preventDefault();
  };

  return (
    <React.Fragment>
      <form onSubmit={onSubmitHandler}>
        <label htmlFor="regex">Regex</label>
        <input type="text" id="regex"></input>
        <label htmlFor="targetString">Target String</label>
        <input type="text" id="targetString"></input>
        <button type="submit">Submit</button>
      </form>
    </React.Fragment>
  );
};

export default App;
